
public class ArrayDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// declare array
		int nArray[];
		//allocate array
		nArray = new int[10];
		//initialize array
		nArray[0] = 100;
		nArray[9] = 1000;
		
		
		System.out.println("Element at index 0 : " + nArray[0]);
		System.out.println("Element at index 9 : " + nArray[9]); 
		
		
	}

}
