
public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5; 
		int b = 7;
		
		//bitwise
		// 0101 & 0111 = 0101 = 5
		System.out.println("a&b = " + (a &b));
		//0101 |0111 = 0111 = 7
		System.out.println("a|b = " + (a | b));
	}

}
