
public class DowhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 11;
		
		do {
			System.out.println("Count is: "+ count);
			count++;
		}while(count<=10);
		
		while (count < 15) {
			System.out.println("Count is : " + count);
			count++;
			
		}
		
	}

}
