
public class PrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a = 'A';
		byte b = 2;
		short c = 22;
		int d = 45;
		float e = 5.123451f;
		double f = 4.6828123434343d;
		boolean g = true;
		
		System.out.println("Char: " + a);
		System.out.println("byte: " + b);
		System.out.println("double: " + f);
	}

}
