
public class operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 1 + 2; 
		System.out.println("1 + 2 : " + result);
		int result1 = 1 - 2; 
		System.out.println("1 - 2 : " + result1);
		int result2 = 10 % 2; 
		System.out.println("1 - 2 : " + result2 );
		
	}

}
